import React from 'react';
import { Link } from 'react-router-dom';

function Header() {
  const [showInfo, setShowInfo] = React.useState(false);

  const togglePopup = () => {
    setShowInfo(!showInfo);
  };

  return (
    <div className="header">
      <div className="header-content">
        <Link to="/" style={{ textDecoration: 'none', display: 'flex', alignItems: 'center' }}>
          <img 
            src="/favicon.ico" 
            alt="Suduoku Logo" 
            className="header-logo" 
          />
          <h1 className="header-title">Suduoku</h1>
          
        </Link>
        <h1 className="header-info" onClick={togglePopup}>&times;</h1>
        {showInfo && (
          <div className="info-popup">
            <div className="popup-content">
              <span className="close-popup" onClick={togglePopup}>&times;</span>
              <h2>About Suduoku</h2>
              <p>Suduoku is a collaborative Sudoku game that allows multiple players to solve puzzles together in real-time.</p>
              <p>Each player can see other players' moves and communicate through the built-in chat feature.</p>
              <h3>How to Play</h3>
              <ul>
                <li>Click on an empty cell to select it</li>
                <li>Enter a number from 1-9</li>
                <li>Use the "Check Solution" button to verify your progress</li>
                <li>Collaborate with others to solve the puzzle faster!</li>
              </ul>
              <p>Created by Your Name &copy; 2025</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Header;
