import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

function PuzzleSelection() {
  const [puzzles, setPuzzles] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  
  useEffect(() => {
    fetch('https://2a72-2601-1c2-4503-61b0-62-7a49-5af4-c336.ngrok-free.app/puzzles')
      .then(async response => {
        // Get the raw response text
        const rawText = await response.text();
        
        // Extract JSON from potentially HTML-contaminated response
        function extractJSON(text) {
          // Try direct parsing first (maybe it's clean)
          try {
            return JSON.parse(text);
          } catch (e) {
            // Look for array pattern (your puzzles endpoint returns an array)
            const arrayMatch = text.match(/(\[.*\])/s);
            if (arrayMatch) {
              try {
                return JSON.parse(arrayMatch[0]);
              } catch (arrayError) {
                console.error('Failed to parse array match:', arrayError);
              }
            }
            
            // Try to find any JSON object
            const objectMatch = text.match(/(\{.*\})/s);
            if (objectMatch) {
              try {
                return JSON.parse(objectMatch[0]);
              } catch (objectError) {
                console.error('Failed to parse object match:', objectError);
              }
            }
            
            throw new Error('Could not extract valid JSON from response');
          }
        }
  
        try {
          const data = extractJSON(rawText);
          console.log('Successfully extracted JSON data:', data);
          return data;
        } catch (parseError) {
          console.error('JSON extraction failed:', parseError);
          console.error('Raw response (first 200 chars):', rawText.substring(0, 200));
          throw parseError;
        }
      })
      .then(data => {
        console.log('Setting puzzles with data:', data);
        setPuzzles(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching puzzles:', err);
        setLoading(false);
      });
  }, []);

  const handlePuzzleSelect = (puzzleId) => {
    navigate(`/puzzle/${puzzleId}`);
  };

  if (loading) {
    return (
      <div>
        <Header />
        <div className="container">
          <h2>Loading puzzles...</h2>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header />
      <div className="container">        
        <div className="puzzle-grid">
          {puzzles.map(puzzle => (
            <div 
              key={puzzle.id} 
              className="puzzle-card"
              onClick={() => handlePuzzleSelect(puzzle.id)}
            >
              <h3>{puzzle.title}</h3>
              <div className="puzzle-meta">
                <span className="difficulty">{puzzle.difficulty || 'Medium'}</span>
                <span className="status">{puzzle.status || 'New'}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PuzzleSelection;
