import React, { useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import './index.css';
import SudokuGame from './components/SudokuGame';
import PuzzleSelection from './components/PuzzleSelection';

// Get the base URL for GitHub Pages
const basename = process.env.PUBLIC_URL || '';

function RedirectHandler() {
  const navigate = useNavigate();

  useEffect(() => {
    // Check if there's a stored path in sessionStorage
    const redirectPath = sessionStorage.getItem('redirectPath');
    if (redirectPath) {
      // Clear the stored path
      sessionStorage.removeItem('redirectPath');

      // Navigate to the stored path (without the basename)
      const relativePath = redirectPath.replace(basename, '');

      // Small timeout to ensure app is fully loaded
      setTimeout(() => {
        navigate(relativePath);
      }, 100);
    }
  }, [navigate]);

  return null;
}

function App() {
  return (
    <BrowserRouter basename={basename}>
      {/* Add the RedirectHandler at the top level */}
      <RedirectHandler />
      <Routes>
        <Route path="/" element={<PuzzleSelection />} />
        <Route path="/puzzle/:puzzleId" element={<SudokuGame />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </BrowserRouter>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
