import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

function PuzzleSelection() {
  const [puzzles, setPuzzles] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  
  useEffect(() => {
    // Use relative URL instead of hardcoded localhost
    fetch('http://localhost:8080/puzzles')
    //fetch('https://2a72-2601-1c2-4503-61b0-62-7a49-5af4-c336.ngrok-free.app/puzzles')
      .then(response => response.json())
      .then(data => {
        setPuzzles(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching puzzles:', err);
        setLoading(false);
      });
  }, []);

  const handlePuzzleSelect = (puzzleId) => {
    navigate(`/puzzle/${puzzleId}`);
  };

  if (loading) {
    return (
      <div>
        <Header />
        <div className="container">
          <h2>Loading puzzles...</h2>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header />
      <div className="container">        
        <div className="puzzle-grid">
          {puzzles.map(puzzle => (
            <div 
              key={puzzle.id} 
              className="puzzle-card"
              onClick={() => handlePuzzleSelect(puzzle.id)}
            >
              <h3>{puzzle.title}</h3>
              <div className="puzzle-meta">
                <span className="difficulty">{puzzle.difficulty || 'Medium'}</span>
                <span className="status">{puzzle.status || 'New'}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PuzzleSelection;
