import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

function PuzzleSelection() {
  const [puzzles, setPuzzles] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  
  useEffect(() => {
    // Get base URL from current location (works with Ngrok)
    const baseUrl = window.location.origin;
    console.log('Using base URL:', baseUrl);
    
    // Use the base URL to make API requests
    fetch(`${baseUrl}/puzzles`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`Server returned ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Puzzles loaded:', data.length);
        setPuzzles(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching puzzles:', err);
        setLoading(false);
      });
  }, []);

  const handlePuzzleSelect = (puzzleId) => {
    navigate(`/puzzle/${puzzleId}`);
  };

  if (loading) {
    return (
      <div>
        <Header />
        <div className="container">
          <h2>Loading puzzles...</h2>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header />
      <div className="container">
        <div className="puzzle-grid">
          {puzzles.map(puzzle => (
            <div 
              key={puzzle.id} 
              className="puzzle-card"
              onClick={() => handlePuzzleSelect(puzzle.id)}
            >
              <h3>{puzzle.title}</h3>
              <div className="puzzle-meta">
                <span className="difficulty">{puzzle.difficulty || 'Medium'}</span>
                <span className="status">{puzzle.status || 'New'}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PuzzleSelection;
